import { Link } from "wouter";
import { Product } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Heart, ShoppingBag } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import { useLanguage } from "@/contexts/LanguageContext";

interface ProductCardProps {
  product: Product;
}

export default function ProductCard({ product }: ProductCardProps) {
  const { t } = useLanguage();

  // Fetch real-time price for this product
  const { data: priceData } = useQuery<{
    productId: string;
    weight: number;
    purity: string;
    material: string;
    ratePerGram: string;
    totalPrice: string;
    updatedAt: string;
  }>({
    queryKey: ["/api/products", product.id, "price"],
    refetchInterval: 60000, // Refetch every minute
  });

  const getBadgeColor = () => {
    if (product.featured === 1) return "bg-gold text-white";
    if (product.material === "gold") return "bg-amber-100 text-amber-800";
    if (product.material === "silver") return "bg-gray-100 text-gray-800";
    return "bg-blue-100 text-blue-800";
  };

  const getButtonClass = () => {
    return product.material === "gold" ? "gold-gradient" : "silver-gradient";
  };

  return (
    <div className="bg-white rounded-2xl shadow-lg overflow-hidden group hover:shadow-2xl transition-all duration-300">
      <div className="relative overflow-hidden">
        <img
          src={product.imageUrl}
          alt={product.name}
          className="w-full h-64 object-cover group-hover:scale-105 transition-transform duration-300"
          loading="lazy"
          onError={(e) => {
            const img = e.target as HTMLImageElement;
            img.src = 'https://images.unsplash.com/photo-1515562141207-7a88fb7ce338?w=400&h=300&fit=crop&crop=center';
          }}
        />
        <div className="absolute top-4 right-4">
          {product.featured === 1 ? (
            <Badge className={getBadgeColor()}>Featured</Badge>
          ) : (
            <Badge variant="secondary" className={getBadgeColor()}>
              {product.material === "gold" ? "Gold" : "Silver"}
            </Badge>
          )}
        </div>
        <button className="absolute top-4 left-4 w-8 h-8 bg-white rounded-full flex items-center justify-center opacity-0 group-hover:opacity-100 transition-all duration-300 hover:bg-red-50">
          <Heart className="w-4 h-4 text-gray-600 hover:text-red-500 transition-colors" />
        </button>
      </div>

      <div className="p-6">
        <h4 className="font-semibold text-lg text-navy mb-2 line-clamp-1">{product.name}</h4>
        <p className="text-gray-600 text-sm mb-3 line-clamp-2">{product.description}</p>

        <div className="flex flex-col space-y-2 mb-4">
          <div className="flex justify-between items-center">
            <span className="text-lg font-bold text-gold">{product.weight}g</span>
            <span className="text-sm text-gray-500">{product.purity}</span>
          </div>
          {priceData && (
            <div className="flex justify-between items-center">
              <span className="text-xs text-gray-500">
                â‚¹{priceData.ratePerGram}/{t('product.weight').toLowerCase()}
              </span>
              <span className="text-lg font-bold text-navy">
                â‚¹{priceData.totalPrice}
              </span>
            </div>
          )}
          {product.region && (
            <div className="text-xs text-gray-500 capitalize">
              {product.region} Style
            </div>
          )}
        </div>

        <div className="flex space-x-2">
          <Link href={`/product/${product.id}`} className="flex-1">
            <Button 
              className={`${getButtonClass()} text-white w-full py-2 px-4 rounded-lg text-sm font-medium hover:shadow-lg transition-all duration-300`}
            >
              View Details
            </Button>
          </Link>
          <Button 
            variant="outline" 
            size="sm"
            className="w-10 h-10 p-0 border-gold text-gold hover:bg-gold hover:text-white transition-all duration-300"
          >
            <ShoppingBag className="w-4 h-4" />
          </Button>
        </div>
      </div>
    </div>
  );
}

client/src/components/RatesDisplay.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Rate } from "@shared/schema";
import { TrendingUp, TrendingDown } from "lucide-react";

export default function RatesDisplay() {
  const { data: rates } = useQuery<Rate[]>({
    queryKey: ["/api/rates"],
    refetchInterval: 30000, // Refetch every 30 seconds
  });

  const goldRate = rates?.find(rate => rate.material === "gold");
  const silverRate = rates?.find(rate => rate.material === "silver");

  const formatChange = (change: string) => {
    const changeNum = parseFloat(change);
    const isPositive = changeNum >= 0;
    return {
      value: Math.abs(changeNum).toFixed(2),
      isPositive,
      icon: isPositive ? TrendingUp : TrendingDown,
      color: isPositive ? "text-green-400" : "text-red-400"
    };
  };

  return (
    <section className="bg-navy text-white py-4">
      <div className="container mx-auto px-4">
        <div className="flex flex-col md:flex-row justify-center items-center space-y-4 md:space-y-0 md:space-x-8 text-center">
          {goldRate && (
            <div className="flex items-center space-x-2">
              <i className="fas fa-coins text-gold text-xl"></i>
              <span className="font-medium">Gold (24K):</span>
              <span className="font-bold text-gold text-lg">â‚¹{parseFloat(goldRate.rate).toFixed(0)}/gram</span>
              {(() => {
                const change = formatChange(goldRate.change);
                const Icon = change.icon;
                return (
                  <span className={`${change.color} text-sm flex items-center`}>
                    <Icon className="w-4 h-4 mr-1" />
                    â‚¹{change.value}
                  </span>
                );
              })()}
            </div>
          )}

          <div className="hidden md:block w-px h-6 bg-gray-500"></div>

          {silverRate && (
            <div className="flex items-center space-x-2">
              <i className="fas fa-coins text-silver text-xl"></i>
              <span className="font-medium">Silver:</span>
              <span className="font-bold text-silver text-lg">â‚¹{parseFloat(silverRate.rate).toFixed(1)}/gram</span>
              {(() => {
                const change = formatChange(silverRate.change);
                const Icon = change.icon;
                return (
                  <span className={`${change.color} text-sm flex items-center`}>
                    <Icon className="w-4 h-4 mr-1" />
                    â‚¹{change.value}
                  </span>
                );
              })()}
            </div>
          )}

          <div className="hidden lg:block">
            <span className="text-sm text-gray-300">
              Updated: {goldRate ? new Date(goldRate.updatedAt).toLocaleTimeString() : "Loading..."}
            </span>
          </div>
        </div>
      </div>
    </section>
  );
}

client/src/components/SignupForm.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { Eye, EyeOff } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface SignupFormProps {
  onSuccess: () => void;
  onToggleMode: () => void;
}

export default function SignupForm({ onSuccess, onToggleMode }: SignupFormProps) {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const { toast } = useToast();

  const signupMutation = useMutation({
    mutationFn: async (userData: Omit<typeof formData, 'confirmPassword'>) => {
      const response = await apiRequest("/api/auth/signup", {
        method: "POST",
        body: JSON.stringify(userData),
      });
      return response.json();
    },
    onSuccess: (data) => {
      localStorage.setItem("token", data.token);
      onSuccess();
      toast({
        title: "Account Created",
        description: `Welcome, ${data.user.firstName}! Your account has been created successfully.`,
      });
    },
    onError: (error: any) => {
      toast({
        title: "Signup Failed",
        description: error.message || "Failed to create account",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      toast({
        title: "Password Mismatch",
        description: "Passwords do not match",
        variant: "destructive",
      });
      return;
    }

    if (formData.password.length < 6) {
      toast({
        title: "Password Too Short",
        description: "Password must be at least 6 characters long",
        variant: "destructive",
      });
      return;
    }

    const { confirmPassword, ...userData } = formData;
    signupMutation.mutate(userData);
  };

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader>
        <CardTitle className="text-center text-navy">Create Your Account</CardTitle>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="firstName">First Name</Label>
              <Input
                id="firstName"
                type="text"
                value={formData.firstName}
                onChange={(e) => handleInputChange('firstName', e.target.value)}
                placeholder="Enter your first name"
                required
              />
            </div>

            <div>
              <Label htmlFor="lastName">Last Name</Label>
              <Input
                id="lastName"
                type="text"
                value={formData.lastName}
                onChange={(e) => handleInputChange('lastName', e.target.value)}
                placeholder="Enter your last name"
                required
              />
            </div>
          </div>

          <div>
            <Label htmlFor="email">Email</Label>
            <Input
              id="email"
              type="email"
              value={formData.email}
              onChange={(e) => handleInputChange('email', e.target.value)}
              placeholder="Enter your email"
              required
            />
          </div>

          <div>
            <Label htmlFor="password">Password</Label>
            <div className="relative">
              <Input
                id="password"
                type={showPassword ? "text" : "password"}
                value={formData.password}
                onChange={(e) => handleInputChange('password', e.target.value)}
                placeholder="Enter your password"
                required
              />
              <Button
                type="button"
                variant="ghost"
                size="sm"
                className="absolute right-2 top-1/2 transform -translate-y-1/2"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
              </Button>
            </div>
          </div>

          <div>
            <Label htmlFor="confirmPassword">Confirm Password</Label>
            <div className="relative">
              <Input
                id="confirmPassword"
                type={showConfirmPassword ? "text" : "password"}
                value={formData.confirmPassword}
                onChange={(e) => handleInputChange('confirmPassword', e.target.value)}
                placeholder="Confirm your password"
                required
              />
              <Button
                type="button"
                variant="ghost"
                size="sm"
                className="absolute right-2 top-1/2 transform -translate-y-1/2"
                onClick={() => setShowConfirmPassword(!showConfirmPassword)}
              >
                {showConfirmPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
              </Button>
            </div>
          </div>

          <Button
            type="submit"
            className="w-full gold-gradient text-white"
            disabled={signupMutation.isPending}
          >
            {signupMutation.isPending ? "Creating Account..." : "Create Account"}
          </Button>

          <div className="text-center">
            <button
              type="button"
              onClick={onToggleMode}
              className="text-sm text-gold hover:underline"
            >
              Already have an account? Sign in
            </button>
          </div>
        </form>
      </CardContent>
    </Card>
  );
}

